import React, { useState } from 'react';
import {
   StyleSheet,
   View,
   TextInput,
   Button,
   FlatList
} from 'react-native';
import GoalsInput from './components/GoalsInput';
import GoalItem from './components/GoalItem'


export default function App() {
   // user's goal order
   const [count, setCount] = useState(1);
   // list of goals of user
   const [goalList, setGoalList] = useState([]);

   const inputGoalHandler = input => {
      setInputGoal(input);
   }

   const addGoal = (inputGoal, setInputGoal) => {
      setGoalList([...goalList, {
         id: Math.random().toString(), value: count +
            ". " + inputGoal
      }]);
      setCount(count + 1);
      setInputGoal("");
      // difference between above and below code
      //setGoalList(curr => [...curr, inputGoal]);
   }

   return (
      <View style={styles.screen}>
         <GoalsInput addGoal={addGoal} />
         <FlatList
            data={goalList}
            renderItem={itemData => <GoalItem value={itemData.item.value} />}>
         </FlatList>
      </View>
   );
}

const styles = StyleSheet.create({
   screen: {
      padding: 50
   },

   input_container: {
      flexDirection: 'row',
      alignItems: 'center',
      justifyContent: 'space-between'
   },

   input: {
      width: '87%',
      borderColor: "black",
      borderWidth: 1,
      padding: 10
   },
});
